middleware.js:
import { corsMiddleware } from './lib/corsMiddleware'

/**
 * Middleware function that processes incoming requests through multiple middleware layers
 * @param request - The incoming NextRequest object representing the HTTP request.
 * @returns - NextResponse or undefined depending on the middleware logic.
 * If any middleware fails, it returns a response with an appropriate status code.
 * If all middlewares succeed, the request proceeds to the next handler.
 */
export function middleware(request) {
	const corsResponse = corsMiddleware(request)
	if (corsResponse !== undefined) return corsResponse
}

export const config = {
	matcher: ['/api/:path*'], // Match any path starting with /api/
}

LogExecEnv.js:
/**
 * This function shows in console if the current component is rendered in client or server
 * @param componentName - The name of the component to log
 * @returns null
 */

export function LogExecEnv({ componentName }) {
	const message =
		typeof window === 'undefined'
			? `[${componentName}] executes in SERVER (SSR)`
			: `[${componentName}] executes in CLIENT (Browser)`

	console.log(message)
	return null
}

useScrollToRef.js:
import { RefObject } from 'react'

/**
 * Custom hook to scroll to a specific element reference.
 * @param elementRef - The ref of the element to scroll to.
 * @param offset - The offset from the top of the element.
 */
export function useScrollToRef(
	elementRef,
	offset = 0,
) {
	const scroll = () => {
		if (elementRef.current) {
			requestAnimationFrame(() => {
				const elementTop = elementRef.current!.getBoundingClientRect().top + window.scrollY
				const targetScroll = elementTop - offset

				window.scrollTo({
					top: targetScroll,
					behavior: 'smooth',
				})
			})
		}
	}

	return scroll
}

corsMiddleware.js:
import { NextResponse } from 'next/server'

/**
 * List of allowed origins. Add or remove domains as needed.
 */
const allowedOrigins = [
	'https://acme.com/',
	'https://www.acme.com/',
	// 'http://localhost:3000', // Uncomment for local testing
]

/**
 * Middleware to handle CORS
 * @param request NextRequest
 * @returns NextResponse
 */
export function corsMiddleware(request) {
	const origin = request.headers.get('origin') ?? ''

	// Handle OPTIONS requests for preflight
	if (request.method === 'OPTIONS') {
		const response = NextResponse.next()
		response.headers.set('Access-Control-Allow-Methods', 'GET, POST, OPTIONS')
		response.headers.set('Access-Control-Allow-Headers', 'Content-Type')

		if (allowedOrigins.includes(origin)) {
			response.headers.set('Access-Control-Allow-Origin', origin)
		}

		return response
	}

	// Validate origin for other requests
	if (allowedOrigins.includes(origin)) {
		const response = NextResponse.next()
		response.headers.set('Access-Control-Allow-Origin', origin)
		response.headers.set('Access-Control-Allow-Methods', 'GET, POST, OPTIONS')
		response.headers.set('Access-Control-Allow-Headers', 'Content-Type')
		response.headers.set('Access-Control-Allow-Credentials', 'true')
		return response
	}

	// Respond with 403 if origin is not allowed
	return NextResponse.json({ message: 'CORS not allowed for this origin' }, { status: 403 })
}

rateLimiter.js:
// map to track rate limit info per identifier
const rateLimitMap = new Map()

const RATE_LIMIT_CONFIG = {
	maxAttempts: 3,
	windowMs: 15 * 60 * 1000, // 15 minutes time window
}

/**
 * cleans up expired entries from the rate limit map to prevent memory leaks
 * this function is called periodically during rate limit checks
 */
function cleanupExpiredEntries() {
	const now = Date.now()
	for (const [key, entry] of rateLimitMap.entries()) {
		if (now > entry.resetTime) {
			rateLimitMap.delete(key)
		}
	}
}

/**
 * verifies if an IP address is rate limited
 * @param {string} identifier - unique identifier (IP address)
 * @returns {boolean} - true if rate limited, false otherwise
 */
export function isRateLimited(identifier) {
	cleanupExpiredEntries()

	const now = Date.now()
	const entry = rateLimitMap.get(identifier)

	if (!entry) {
		rateLimitMap.set(identifier, {
			count: 1,
			resetTime: now + RATE_LIMIT_CONFIG.windowMs,
		})
		return false
	}

	// check if the time window has passed
	if (now > entry.resetTime) {
		rateLimitMap.set(identifier, {
			count: 1,
			resetTime: now + RATE_LIMIT_CONFIG.windowMs,
		})
		return false
	}

	// check if max attempts exceeded
	if (entry.count >= RATE_LIMIT_CONFIG.maxAttempts) {
		return true
	}

	entry.count += 1
	return false
}

/**
 * obtains rate limit info for a given identifier
 * @param {string} identifier - IP address
 * @returns {{ isLimited: boolean, remaining: number, resetTime: number }} - rate limit status
 */
export function getRateLimitInfo(identifier) {
	cleanupExpiredEntries()

	const now = Date.now()
	const entry = rateLimitMap.get(identifier)

	// if no entry or window has passed, return full quota
	if (!entry || now > entry.resetTime) {
		return {
			isLimited: false,
			remaining: RATE_LIMIT_CONFIG.maxAttempts - 1,
			resetTime: now + RATE_LIMIT_CONFIG.windowMs,
		}
	}

	const remaining = Math.max(0, RATE_LIMIT_CONFIG.maxAttempts - entry.count)
	const isLimited = remaining === 0

	return {
		isLimited,
		remaining,
		resetTime: entry.resetTime,
	}
}

/**
 * Obtains the client's IP address from the request headers
 * @param {Headers} headers - Request Headers
 * @returns {string} The client's IP address
 */
export function getClientIP(headers) {
	// Common headers used by proxies and CDNs to forward the real client IP
	const forwardedFor = headers.get('x-forwarded-for')
	const realIP = headers.get('x-real-ip')
	const remoteAddr = headers.get('x-vercel-forwarded-for') // for vercel

	// Prioritize x-forwarded-for as it may contain a list of IPs
	if (forwardedFor) {
		return forwardedFor.split(',')[0].trim()
	}

	if (realIP) {
		return realIP.trim()
	}

	if (remoteAddr) {
		return remoteAddr.split(',')[0].trim()
	}

	// Fallback - this should not happen in production
	return 'unknown'
}

