reset.css:
/* Global box-sizing and inherited typography */
*,
*::before,
*::after {
	box-sizing: border-box;
	font: inherit;
}

/* Smooth scroll only if reduced motion is not preferred */
html {
	font-size: 100%;
	hanging-punctuation: first last;
	overflow-x: hidden;
}

html:focus-within {
	scroll-behavior: smooth;
}

/* Reduce motion: disables animations for accessibility */
@media (prefers-reduced-motion: reduce) {
	html:focus-within {
		scroll-behavior: auto;
	}

	*,
	*::before,
	*::after {
		animation-duration: 0.01ms !important;
		animation-iteration-count: 1 !important;
		transition-duration: 0.01ms !important;
		scroll-behavior: auto !important;
	}
}

/* Apply smooth scroll if there are :has(:target) elements (limited support) */
@media (prefers-reduced-motion: no-preference) {
	:has(:target) {
		scroll-padding-top: 3rem;
		scroll-behavior: smooth;
	}
}

/* Base body styling */
body {
	line-height: 1.5;
	text-rendering: optimizeSpeed;
	font-family:
		system-ui,
		-apple-system,
		BlinkMacSystemFont,
		'Segoe UI',
		Roboto,
		'Helvetica Neue',
		sans-serif;
}

/* Basic links */
a {
	text-decoration: none;
	cursor: pointer;
}

/* Improve accessibility for focus */
:focus-visible {
	outline: 2px solid currentColor;
	outline-offset: 2px;
}

/* Responsive media */
img,
picture,
video,
svg {
	display: block;
	max-width: 100%;
	height: auto;
}

/* Base image styling */
img {
	vertical-align: middle;
	font-style: italic;
	background-repeat: no-repeat;
	background-size: cover;
	shape-margin: 0.75rem;
}

/* Remove number input spinners */
input[type='number']::-webkit-inner-spin-button,
input[type='number']::-webkit-outer-spin-button {
	-webkit-appearance: none;
	margin: 0;
}

input[type='number'] {
	appearance: textfield;
}

/* Buttons and button-like elements */
button,
[role='button'] {
	cursor: pointer;
}

/* Textarea settings */
textarea {
	resize: vertical;
}

/* Basic fieldset structure without margin/padding */
fieldset {
	border: none;
	padding: 0;
	margin: 0;
}

/* Balanced headings */
h1,
h2,
h3,
h4,
h5,
h6 {
	text-wrap: balance;
}

/* Readable paragraphs and list items */
p,
li {
	max-width: 65ch;
	text-wrap: pretty;
}

/* Lists without default decoration */
ul,
ol {
	list-style: none;
	padding-left: 0;
}

