import { corsMiddleware } from './lib/corsMiddleware'

/**
 * Middleware function that processes incoming requests through multiple middleware layers
 * @param request - The incoming NextRequest object representing the HTTP request.
 * @returns - NextResponse or undefined depending on the middleware logic.
 * If any middleware fails, it returns a response with an appropriate status code.
 * If all middlewares succeed, the request proceeds to the next handler.
 */
export function middleware(request) {
	const corsResponse = corsMiddleware(request)
	if (corsResponse !== undefined) return corsResponse
}

