corsMiddleware.js:
import { NextResponse } from 'next/server'

/**
 * List of allowed origins. Add or remove domains as needed.
 */
const allowedOrigins = [
	'https://yourdomain1.com',
	'https://yourdomain2.com',
	// 'http://localhost:3000', // Uncomment for local testing
]

/**
 * Middleware to handle CORS
 * @param request NextRequest
 * @returns NextResponse
 */
export function corsMiddleware(request) {
	const origin = request.headers.get('origin') ?? ''

	// Handle OPTIONS requests for preflight
	if (request.method === 'OPTIONS') {
		const response = NextResponse.next()
		response.headers.set('Access-Control-Allow-Methods', 'GET, POST, OPTIONS')
		response.headers.set('Access-Control-Allow-Headers', 'Content-Type')

		if (allowedOrigins.includes(origin)) {
			response.headers.set('Access-Control-Allow-Origin', origin)
		}

		return response
	}

	// Validate origin for other requests
	if (allowedOrigins.includes(origin)) {
		const response = NextResponse.next()
		response.headers.set('Access-Control-Allow-Origin', origin)
		response.headers.set('Access-Control-Allow-Methods', 'GET, POST, OPTIONS')
		response.headers.set('Access-Control-Allow-Headers', 'Content-Type')
		response.headers.set('Access-Control-Allow-Credentials', 'true')
		return response
	}

	// Respond with 403 if origin is not allowed
	return NextResponse.json({ message: 'CORS not allowed for this origin' }, { status: 403 })
}

