@tailwind base;
@tailwind components;
@tailwind utilities;

/*
1. position
2. box model
3. typography
4. visual
5. others
*/

/* Box sizing rules */
*,
*::before,
*::after {
	box-sizing: border-box;
}

/* Remove default margin, padding and font */
/* prevent grid/flex items from spilling out of their container */
* {
	margin: 0;
	padding: 0;
	min-width: 0;
	font: inherit;
}

/* improves punctuation in supported browsers */
html {
	font-size: 100%;
	hanging-punctuation: first last;
	overflow-x: hidden;
}

/* Set core root defaults */
html:focus-within {
	scroll-behavior: smooth;
}

/* Remove all animations, transitions and smooth scroll for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
	html:focus-within {
		scroll-behavior: auto;
	}

	*,
	*::before,
	*::after {
		animation-duration: 0.01ms !important;
		animation-iteration-count: 1 !important;
		transition-duration: 0.01ms !important;
		scroll-behavior: auto !important;
	}
}

/* Add smooth scroll on targets*/
/*for sticky header or just add space*/
@media (prefers-reduced-motion: no-preference) {
	:has(:target) {
		scroll-padding-top: 3rem;
		scroll-behavior: smooth;
	}
}

/* Set core body defaults */
body {
	/* Ensure that the body fills the entire viewport. */
	min-height: 100dvh;
	line-height: 1.5;
	text-rendering: optimizeSpeed;
}

/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role='list'],
ol[role='list'] {
	list-style: none;
}

/* Remove decoration to anchor elements  */
a,
a:visited,
a:active {
	text-decoration: none;
}

/* Anchor elements that don't have a class get default styles */
a:not([class]) {
	text-decoration-skip-ink: auto;
}

/* Responsive images/videos */
img,
picture,
svg,
video {
	display: block;
	max-width: 100%;
}

img {
	height: auto;
	vertical-align: middle;
	font-style: italic;
	background-repeat: no-repeat;
	background-size: cover;
	shape-margin: 0.75rem;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
	font: inherit;
}

/* spin button off for webkit (chrome, safari) */
input[type='number']::-webkit-inner-spin-button,
input[type='number']::-webkit-outer-spin-button {
	-webkit-appearance: none;
	margin: 0;
}

input[type='number'] {
	-moz-appearance: textfield; /* spin button off for firefox */
	appearance: textfield; /* standard */
}

